mapper.select:
SELECT a.id AS "id", a.log_type AS "type", a.log_title AS "title", a.create_time AS "createDate", a.remote_addr AS "remoteAddr", a.user_agent AS "userAgent", a.request_uri AS "requestUri", a.method AS "method", a.params AS "params", a.exception AS "exception", u.id AS "createBy.id", u.name AS "createBy.name" FROM bop_sys_log a JOIN bop_sys_user u ON u.id = a.create_by WHERE a.create_date BETWEEN #{beginDate} AND #{endDate}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.create_by = #{createBy.id}
AND a.request_uri LIKE '%'||#{requestUri}||'%' '%'+#{requestUri}+'%' CONCAT('%', #{requestUri}, '%')
AND a.type = #{TYPE_EXCEPTION}
ORDER BY a.create_date DESC
