mapper.update:
UPDATE bop_sys_menu SET
	parent_id = #{parentId},
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE bop_sys_menu SET
	menu_sort = #{sort}
WHERE id = #{id}

UPDATE bop_sys_menu SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE CONCAT('%,', #{id}, ',%')

mapper.select:
SELECT

FROM bop_sys_menu a

WHERE a.id = #{id}

SELECT
FROM bop_sys_menu a
INNER JOIN bop_sys_role_menu rm ON rm.menu_id = a.id INNER JOIN bop_sys_user_role ur ON ur.role_id = rm.role_id WHERE ur.user_id = #{userId};
